
public class Time {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
long time = System.currentTimeMillis()/1000;

int cp = 24*60*60;
System.out.print(time/cp +":");

time= time % cp;
cp = 60*60;
System.out.print(time/cp +":");

time= time % cp;
cp = 60;
System.out.print(time/cp +":");

System.out.print(time/cp);

	}

}
